import React,{Fragment} from 'react';



class Formulaire extends React.Component {

    state ={
        username : '',
        color: '',
        colors : ["","red","blue","green"],
        comment: ''
    }

    handlerPseudo = (e) => {
        //console.log(e.target.value)
        this.setState({
            username : e.target.value
        })
    }

    handelColor =(event) => {
        this.setState({
            color : event.target.value
        })
    }

    handlerComment = (e) => {
        this.setState({
            Comment : e.target.value
        })
    }

    render(){

        return (
            <Fragment>
                <form action="">
                    <div>
                    <label htmlFor="">Pseudo</label>
                    <input type="text" value = {this.state.username} onChange = {this.handlerPseudo}/>
                    </div>
                    <div>
                        <select value = {this.state.color} onChange ={this.handelColor}>
                        {
                                this.state.colors.map((color,index) => { 
                                return <option hey = {index} value = {color}>{color}</option>
                                })
                        }
                        </select>

                    </div>
                    <div>
                        <label htmlFor="">Commentaire : </label>
                        <textarea value = "{"  onChange={this.handlerComment} cols="30" rows="10"></textarea>
                    </div>
                </form>
            </Fragment>
        )

    }


}

export default Formulaire ;
