import React,{Component} from 'react';
import Car from './Cars'


class MyCard extends Component {

    state = {
        voitures :[
            {
                "name": "ford",
                "color":"blue",
                "date":"2012"
            },
            {
                "name": "bmw",
                "color":"rouge",
                "date":"2000"
            },
            {
                "name": "panda",
                "color":"gris",
                "date":"2005"
            },
        ]
    }

    // noCopie = () => {
    //     alert('ce text est proteger')
    // } 

    addStyle = (e) =>{
        console.log(e.target)
        e.target.classList.toggle('styled')

    }

    addTenYears =(e)=>{
        this.setState(this.state.voitures.date.value += 10)
    }


    render() {
        return (

            <div >
                <h1 onMouseOver ={this.addStyle}>{this.props.titre}</h1>
                {/* <p onCopy = {this.noCopie}>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Id, esse sit accusantium, fugiat dolorem rem nostrum similique voluptatibus laudantium odit iusto vel placeat inventore temporibus exercitationem quos aperiam minima optio!
                </p> */}
                <button onClick = {this.addTenYears}></button>
                <Car color ={this.state.voitures[0].color} annee = {this.state.voitures[0].date} >{this.state.voitures[0].name}</Car>
                <Car color ={this.state.voitures[1].color} annee ={this.state.voitures[1].date}>{this.state.voitures[1].name}</Car>
                <Car color = {this.state.voitures[2].color} annee ={this.state.voitures[2].date}>{this.state.voitures[2].name}</Car>



            </div>
        )
    }
}

export default MyCard;
